import { useState } from "react";

const reducer = (state, action) => {
  switch (
    action.type //action은 객체
  ) {
    case "SUBMIT":
      action.payload.preventDefault();
      const name = action.payload.target.name.value;
      const price = action.payload.target.price.value;
      return [
        ...state,
        { id: Math.random() * 100000, name: name, price: price },
      ];
    default:
      return state;
  }
};
export default reducer;
